mapper.select:
select * from sky_take_out.orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from sky_take_out.orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}

select count(id) from sky_take_out.orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}

select od.name,sum(od.number) number from sky_take_out.order_detail od,sky_take_out.orders o where od.order_id = o.id and status = 5
and o.order_time >= #{begin}
and o.order_time <= #{end}
group by od.id order by number desc limit 0,10
